var Pos=function(){this.x=0;this.y=0;};var Block=function(type,initPos){this.blockType=type;this.pos=new Pos();this.pos.x=initPos.x;this.pos.y=initPos.y;this.style=0;this.Down=function(){this.pos.y--;return this.pos;};this.MoveX=function(x){this.pos.x+=x;return this.pos;};this.GetType=function(){return this.blockType;};this.GetStyle=function(){return this.style;};this.Rotate=function(){switch(this.blockType){case 0:break;case 1:if(this.style==0)
this.style++;else
this.style--;break;case 2:case 3:case 4:if(this.style==3)
this.style=0;else
this.style++;break;case 5:case 6:if(this.style==0)
this.style++;else
this.style--;break;}
return this.style;};this.GetPos=function(){return this.pos;};this.SetPos=function(newPos){this.pos=newPos;};this.getColor=function(){return this.color;};this.setColor=function(newColor){this.color=newColor;};};